/**@jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import React from "react";
import { Company, getCompanyAsync } from "../data/companies";
import { useParams } from "react-router-dom";
import Page from "./Page";
import { Form, FormGroup, Button } from "reactstrap";
import { useForm } from "react-hook-form";
import {
  FieldContainer,
  FieldInput,
  FieldLabel,
  Fieldset,
} from "./Styles/Styles";

interface Props {
  accountNumber: number;
}

type FormData = {
  companyId: number;
  licenseCount: number;
  mobileLicenseCount: number;
  trialLicenseCount: number;
};

const Dealer = () => {
  const { accountNumber } = useParams();

  const [company, setCompany] = React.useState<Company | undefined>(undefined);

  const { register, handleSubmit, formState } = useForm<FormData>({
    mode: "onBlur",
  });

  React.useEffect(() => {
    const doGetCompany = async (accountNumber: number) => {
      console.log(accountNumber);
      let company = await getCompanyAsync(accountNumber);

      setCompany(company);
    };

    doGetCompany(Number(accountNumber));
  }, [accountNumber]);

  const submitForm = async () => {
    console.log();
  };

  return (
    <Page title={`Update licenses from ${company?.CompanyName}`}>
      <div
        css={css`
          text-align: center;
        `}
      >
        <Form
          onSubmit={handleSubmit(submitForm)}
          disabled={formState.isSubmitting}
        >
          <input type="text" value={company?.CompanyId} hidden readOnly />
          <Fieldset>
            <FieldContainer>
              <FieldLabel htmlFor="licenseCount">Desktop Licenses</FieldLabel>
              <FieldInput
                type="number"
                value={company?.Licenses}
                ref={register}
                id="licenseCount"
                name="licenseCount"
              />
            </FieldContainer>
            <FieldContainer>
              <FieldLabel htmlFor="mobileCount">Mobile Licenses</FieldLabel>
              <FieldInput
                ref={register}
                type="number"
                value={company?.LicensesMobileCount}
                id="mobileLicenseCount"
                name="mobileLicenseCount"
              />
            </FieldContainer>
            <FieldContainer>
              <FieldLabel htmlFor="trialCount">Trial Licenses</FieldLabel>
              <FieldInput
                ref={register}
                type="number"
                value={company?.TrialLicenses}
                id="trialLicenseCount"
                name="trialLicenseCount"
              />
            </FieldContainer>
            <Button color="primary">Submit</Button>
          </Fieldset>
        </Form>
      </div>
    </Page>
  );
};

export default Dealer;
