/**@jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import React from "react";
import { Company, getCompanyAsync } from "../data/companies";
import { useParams } from "react-router-dom";
import Page from "./Page";
import { Form, Label, Input, FormGroup, Button } from "reactstrap";
import { useForm } from "react-hook-form";

interface Props {
  accountNumber: number;
}

type FormData = {
  companyId: number;
  licenseCount: number;
  mobileLicenseCount: number;
  trialLicenseCount: number;
};

const Dealer = () => {
  const { accountNumber } = useParams();

  const [company, setCompany] = React.useState<Company | undefined>(undefined);

  const { register, handleSubmit, formState } = useForm<FormData>();

  React.useEffect(() => {
    const doGetCompany = async (accountNumber: number) => {
      console.log(accountNumber);
      let company = await getCompanyAsync(accountNumber);

      setCompany(company);
    };

    doGetCompany(Number(accountNumber));
  }, [accountNumber]);

  const submitForm = async () => {
    console.log();
  };

  return (
    <Page title={`Update licenses from ${company?.CompanyName}`}>
      <div
        css={css`
          text-align: center;
        `}
      >
        <Form
          onSubmit={handleSubmit(submitForm)}
          disabled={formState.isSubmitting}
        >
          <FormGroup row className="d-flex justify-content-space-between">
            <Label className="col-6" for="licenseCount">
              Desktop Licenses
            </Label>
            <Input type="number" value={company?.Licenses} id="licenseCount" />
          </FormGroup>
          <FormGroup row>
            <Label for="mobileCount">Mobile Licenses</Label>
            <Input
              type="number"
              value={company?.LicensesMobileCount}
              id="mobileCount"
            />
          </FormGroup>
          <FormGroup row className="mb-3">
            <Label for="trialCount">Trial Licenses</Label>
            <Input
              type="number"
              value={company?.TrialLicenses}
              id="trialCount"
            />
          </FormGroup>
          <Button color="primary">Submit</Button>
        </Form>
      </div>
    </Page>
  );
};

export default Dealer;
